targets.expr:
sum(
  rate(
    cortex_request_duration_seconds_count{
      cluster=~"$cluster", job=~"($namespace)/((query-frontend.*|cortex|mimir|mimir-read.*))",
      route=~"(prometheus|api_prom)_api_v1_query"
    }[$__rate_interval]
  )
  or
  rate(
    cortex_prometheus_rule_evaluations_total{
      cluster=~"$cluster", job=~"($namespace)/((ruler|cortex|mimir|mimir-backend.*))"
    }[$__rate_interval]
  )
)

max by (scaletargetref_name) (
  kube_horizontalpodautoscaler_spec_max_replicas{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-querier"}
  # Add the scaletargetref_name label which is more readable than "kube-hpa-..."
  + on (cluster, namespace, horizontalpodautoscaler) group_left (scaletargetref_name)
    0*kube_horizontalpodautoscaler_info{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-querier"}
)

max by (scaletargetref_name) (
  kube_horizontalpodautoscaler_status_current_replicas{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-querier"}
  # HPA doesn't go to 0 replicas, so we multiply by 0 if the HPA is not active.
  * on (cluster, namespace, horizontalpodautoscaler)
    kube_horizontalpodautoscaler_status_condition{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-querier", condition="ScalingActive", status="true"}
  # Add the scaletargetref_name label which is more readable than "kube-hpa-..."
  + on (cluster, namespace, horizontalpodautoscaler) group_left (scaletargetref_name)
    0*kube_horizontalpodautoscaler_info{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-querier"}
)

max by (scaletargetref_name) (
  kube_horizontalpodautoscaler_spec_min_replicas{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-querier"}
  # Add the scaletargetref_name label which is more readable than "kube-hpa-..."
  + on (cluster, namespace, horizontalpodautoscaler) group_left (scaletargetref_name)
    0*kube_horizontalpodautoscaler_info{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-querier"}
)

