(1 - (
  avg_over_time(histogram_quantile(0.99, sum by (le) (cluster_job_route:cortex_request_duration_seconds_bucket:sum_rate{cluster=~"$cluster", job=~"($namespace)/((distributor.*|cortex|mimir))", route=~"api_(v1|prom)_push|otlp_v1_metrics|api_v1_push_influx_write"} offset 24h))[1h:])
  /
