targets.expr:
group by (pod, reason) (
  kube_pod_container_status_last_terminated_reason{cluster=~"$cluster", namespace=~"$namespace",container=~"federation-frontend",reason="OOMKilled"}
  unless
  # Note, this leg offsets by the "$__interval" to find the first occurrence of OOMKilled in the gauge.
  (kube_pod_container_status_last_terminated_reason{cluster=~"$cluster", namespace=~"$namespace",container=~"federation-frontend",reason="OOMKilled"} offset $__interval == bool 0)
) != 0
