targets.expr:
sum by(instance, pod, device) (
  rate(
    node_disk_written_bytes_total[$__rate_interval]
  )
)
+
ignoring(pod) group_right() (
  label_replace(
    count by(
      instance,
      pod,
      device
    )
    (
      container_fs_writes_bytes_total{
        cluster=~"$cluster", namespace=~"$namespace",
        container="store-gateway",
        device!~".*sda.*"
      }
    ),
    "device",
    "$1",
    "device",
    "/dev/(.*)"
  ) * 0
)

sum by(instance, pod, device) (
  rate(
    node_disk_read_bytes_total[$__rate_interval]
  )
) + ignoring(pod) group_right() (
  label_replace(
    count by(
      instance,
      pod,
      device
    )
    (
      container_fs_writes_bytes_total{
        cluster=~"$cluster", namespace=~"$namespace",
        container="store-gateway",
        device!~".*sda.*"
      }
    ),
    "device",
    "$1",
    "device",
    "/dev/(.*)"
  ) * 0
)

max by(persistentvolumeclaim) (
  kubelet_volume_stats_used_bytes{cluster=~"$cluster", namespace=~"$namespace"} /
  kubelet_volume_stats_capacity_bytes{cluster=~"$cluster", namespace=~"$namespace"}
)
and
count by(persistentvolumeclaim) (
  kube_persistentvolumeclaim_labels{
    cluster=~"$cluster", namespace=~"$namespace",
    label_name=~"store-gateway.*"
  }
)
