targets.expr:
sum(
  rate(
    cortex_request_duration_seconds_count{
      cluster=~"$cluster", job=~"($namespace)/((query-frontend.*|cortex|mimir))",
      route=~"(prometheus|api_prom)_api_v1_query"
    }[$__rate_interval]
  )
  or
  rate(
    cortex_prometheus_rule_evaluations_total{
      cluster=~"$cluster", job=~"($namespace)/((ruler|cortex|mimir))"
    }[$__rate_interval]
  )
)

sum by(metric) (rate(keda_metrics_adapter_scaler_errors[$__rate_interval])) +
on(metric) group_left
label_replace(
    kube_horizontalpodautoscaler_spec_target_metric{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler="keda-hpa-querier"}
    * 0, "metric", "$1", "metric_name", "(.+)"
)

sum by(operation) (
  rate(
    thanos_memcached_operations_total{
      component="store-gateway",
      name="index-cache",
      cluster=~"$cluster", job=~"($namespace)/((store-gateway.*|cortex|mimir))"
    }[$__rate_interval]
  )
)

sum(rate(thanos_memcached_operation_duration_seconds_sum{
  cluster=~"$cluster", job=~"($namespace)/((store-gateway.*|cortex|mimir))",
  operation="getmulti",
  component="store-gateway",
  name="index-cache"
}
[$__rate_interval])) * 1e3 / sum(rate(thanos_memcached_operation_duration_seconds_count{
  cluster=~"$cluster", job=~"($namespace)/((store-gateway.*|cortex|mimir))",
  operation="getmulti",
  component="store-gateway",
  name="index-cache"
}
[$__rate_interval]))

