targets.expr:
max by (scaletargetref_name) (
  kube_horizontalpodautoscaler_spec_max_replicas{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-(?:mimir-)?querier"}
  # Add the scaletargetref_name label which is more readable than "kube-hpa-..."
  * on (cluster, namespace, horizontalpodautoscaler) group_left (scaletargetref_name)
    group by (cluster, namespace, horizontalpodautoscaler, scaletargetref_name) (kube_horizontalpodautoscaler_info{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-(?:mimir-)?querier"})
)

