targets.expr:
( # Classic storage
  sum by (cluster, namespace, ) (
    cortex_ingester_memory_series{cluster=~"$cluster", job=~"($namespace)/((ingester.*|cortex|mimir))"} unless on (job)
    cortex_partition_ring_partitions{cluster=~"$cluster", job=~"($namespace)/((ingester.*|cortex|mimir))"}
  )
  / on (cluster, namespace) group_left()
  max by (cluster, namespace) (cortex_distributor_replication_factor{cluster=~"$cluster", job=~"($namespace)/((distributor.*|cortex|mimir))"})
)
or
( # Ingest storage
  sum by (cluster, namespace, ) (
    max by (ingester_id, cluster, namespace, ) (
      label_replace(
        cortex_ingester_memory_series{cluster=~"$cluster", job=~"($namespace)/((ingester.*|cortex|mimir))"},
        "ingester_id", "$1", "pod", ".*-([0-9]+)$"
      )
    )
  )
)

