targets.expr:
sum(cortex_ingester_memory_series{cluster=~"$cluster", job=~"($namespace)/((ingester.*|cortex|mimir|mimir-write.*))"}
/ on(cluster, namespace) group_left
max by (cluster, namespace) (cortex_distributor_replication_factor{cluster=~"$cluster", job=~"($namespace)/((distributor.*|cortex|mimir|mimir-write.*))"}))

sum(cortex_ingester_tsdb_exemplar_exemplars_in_storage{cluster=~"$cluster", job=~"($namespace)/((ingester.*|cortex|mimir|mimir-write.*))"}
/ on(cluster, namespace) group_left
max by (cluster, namespace) (cortex_distributor_replication_factor{cluster=~"$cluster", job=~"($namespace)/((distributor.*|cortex|mimir|mimir-write.*))"}))

max by (scaletargetref_name) (
  kube_horizontalpodautoscaler_spec_max_replicas{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-distributor"}
  # Add the scaletargetref_name label for readability
  + on (cluster, namespace, horizontalpodautoscaler) group_left (scaletargetref_name)
    0*kube_horizontalpodautoscaler_info{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-distributor"}
)

max by (scaletargetref_name) (
  kube_horizontalpodautoscaler_status_current_replicas{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-distributor"}
  # HPA doesn't go to 0 replicas, so we multiply by 0 if the HPA is not active
  * on (cluster, namespace, horizontalpodautoscaler)
    kube_horizontalpodautoscaler_status_condition{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-distributor", condition="ScalingActive", status="true"}
  # Add the scaletargetref_name label for readability
  + on (cluster, namespace, horizontalpodautoscaler) group_left (scaletargetref_name)
    0*kube_horizontalpodautoscaler_info{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-distributor"}
)

max by (scaletargetref_name) (
  kube_horizontalpodautoscaler_spec_min_replicas{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-distributor"}
  # Add the scaletargetref_name label for readability
  + on (cluster, namespace, horizontalpodautoscaler) group_left (scaletargetref_name)
    0*kube_horizontalpodautoscaler_info{cluster=~"$cluster", namespace=~"$namespace", horizontalpodautoscaler=~"keda-hpa-distributor"}
)

sum(
  cluster_namespace_job:cortex_ingester_ingested_exemplars:rate5m{cluster=~"$cluster", job=~"($namespace)/((ingester.*|cortex|mimir|mimir-write.*))"}
  / on(cluster, namespace) group_left
  max by (cluster, namespace) (cortex_distributor_replication_factor{cluster=~"$cluster", job=~"($namespace)/((distributor.*|cortex|mimir|mimir-write.*))"})
)

sum(
  cluster_namespace_job:cortex_ingester_tsdb_exemplar_exemplars_appended:rate5m{cluster=~"$cluster", job=~"($namespace)/((ingester.*|cortex|mimir|mimir-write.*))"}
  / on(cluster, namespace) group_left
  max by (cluster, namespace) (cortex_distributor_replication_factor{cluster=~"$cluster", job=~"($namespace)/((distributor.*|cortex|mimir|mimir-write.*))"})
)
