package wecom

import (
	"github.com/grafana/alerting/receivers/schema"
	v1 "github.com/grafana/alerting/receivers/wecom/v1"
)

const Type schema.IntegrationType = "wecom"

func Schema() schema.IntegrationTypeSchema {
	return schema.IntegrationTypeSchema{
		Type:           Type,
		Name:           "WeCom",
		Description:    "Send alerts generated by Grafana to WeCom",
		Heading:        "WeCom settings",
		CurrentVersion: v1.Version,
		Versions: []schema.IntegrationSchemaVersion{
			v1.Schema(),
		},
	}
}
